## Files to Create

### 1. src/app/api/flow/[flowId]/route.ts
```typescript
import { NextResponse } from 'next/server'
import { getFlow } from '@/data/flows'

export async function GET(
  req: Request,
  { params }: { params: { flowId: string } }
) {
  try {
    const flow = getFlow(params.flowId)
    return NextResponse.json(flow)
  } catch (err) {
    return NextResponse.json({ error: 'Flow not found' }, { status: 404 })
  }
}
```

### 2. src/components/flow/StepRenderer.tsx
```typescript
'use client'
import type { FlowDefinition, StepConfig } from '@/data/flows/types'
// Import existing components from this directory (after rename)
import { QuestionStep } from './screens/PhaseFlow'  // or wherever it lives
import { AIMoment } from './screens/PhaseFlow'
import { SalesPage } from './screens/PhaseFlow'

interface Props {
  flow: FlowDefinition
  step: StepConfig | undefined
}

export function StepRenderer({ flow, step }: Props) {
  if (!step) return <div>No step</div>

  switch (step.type) {
    case 'question':
      return <QuestionStep step={step} />
    case 'ai-moment':
      return <AIMoment step={step} flow={flow} />
    case 'sales-page':
      return <SalesPage step={step} flow={flow} />
    default:
      return <div>Unknown step type</div>
  }
}
```

